<script>
export default {
  name: "ErrorLayout",
  props: ["error"],
  head() {
    return {
      link: [
        {
          rel: "canonical",
          hid: "canonical",
          href: `https://daniel-ordonez.com/${
            this.lang === "en" ? "" : "es/"
          }404`,
        },
      ],
    };
  },
  computed: {
    lang() {
      let path = this.$route.path.split("/");
      if (path.length > 1 && path[1] === "es") return "es";
      else return "en";
    },
  },
};
